  private void init() {
     if (D.bookStack.isEmpty()) {
        for (String book : D.books) {
            D.bookStack.push(book);
        }
        for (int i = D.bookStack.size() - 1; i >= 0; i--) {
                DefaultTableModel a = (DefaultTableModel) Table.getModel();

                String book = D.bookStack.get(i);
                a.addRow(new Object[] { book });
    }
  }}
