    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        DefaultTableModel a = (DefaultTableModel) Table.getModel();
        a.setRowCount(0);
         if (D.bookStack.isEmpty()) {
            JOptionPane.showMessageDialog(null, "No books to sort.", "Error",  JOptionPane.PLAIN_MESSAGE);
            return;
        }
        Stack<String> tempStack = new Stack<>();
        while (!D.bookStack.isEmpty()) {
            String temp = D.bookStack.pop();
            while (!tempStack.isEmpty() && tempStack.peek().compareTo(temp) > 0) {
                D.bookStack.push(tempStack.pop());
            }
            tempStack.push(temp);
        }
        D.bookStack = tempStack;
         for (int i = 0 ; i <= D.bookStack.size() - 1; i++) {

                String book = D.bookStack.get(i);
                a.addRow(new Object[] { book });
    }
        JOptionPane.showMessageDialog(null, "Books sorted alphabetically.", "Success",  JOptionPane.PLAIN_MESSAGE);
    }//GEN-LAST:event_jButton7ActionPerformed

